module_preset=\"default\"][et_pb_image src=\"https://microfox.birotamas.hu/wp-content/uploads/2021/08/IMG_8122-scaled.jpg\" title_text=\"IMG_8122\" _builder_version=\"4.7.7\" _module_preset=\"default\" min_height=\"281px\" hover_enabled=\"0\" sticky_enabled=\"0\"][/et_pb_image][et_pb_text _builder_version=\"4.7.7\" _module_preset=\"default\" hover_enabled=\"0\" sticky_enabled=\"0\"]<p style=\"text-align: center;\"><strong>Székesi Krisztián</strong></p>\n<p style=\"text-align: center;\">Tanácsadó</p>\n[/et_pb_text][/et_pb_column][/et_pb_row][/et_pb_section]', 'Programozás', '', 'inherit', 'closed', 'closed', '', '2688-revision-v1', '', '', '2021-08-23 14:58:00', '2021-08-23 14:58:00', '', 2688, 'https://microfox.birotamas.hu/?p=3511', 0, 'revision', '', 0),
