dule_preset=\"default\" background_color=\"rgba(224,43,32,0.2)\" background_color_gradient_start=\"rgba(224,43,32,0)\" background_color_gradient_end=\"#ffffff\" top_divider_style=\"ramp\" bottom_divider_style=\"ramp\" prev_background_color=\"#ffffff\"][et_pb_row column_structure=\"1_2,1_2\" _builder_version=\"4.7.7\" _module_preset=\"default\"][et_pb_column type=\"1_2\" _builder_version=\"4.7.5\" _module_preset=\"default\"][et_pb_image src=\"https://microfox.birotamas.hu/wp-content/uploads/2021/08/IMG_8181-scaled.jpg\" title_text=\"IMG_8181\" _builder_version=\"4.7.7\" _module_preset=\"default\" min_height=\"185px\"][/et_pb_image][et_pb_text _builder_version=\"4.7.7\" _module_preset=\"default\"]<p style=\"text-align: center;\"><strong>Uhrin Gábor</strong></p>\n<p style=\"text-align: center;\">Ügyvezető</p>\n<p>&nbsp;</p>[/et_pb_text][/et_pb_column][et_pb_column type=\"1_2\" _builder_version=\"4.7.5\" _module_preset=\"default\"][et_pb_text _builder_version=\"4.7.7\" _module_preset=\"default\"]<p style=\"text-align: justify;\">Igyekszünk megoldani ügyfeleink minden számítástechnikai jellegű problémáját. Fiatalos, rugalmas gondolkodás jellemez minket. A programozási tevékenységet a Kft. ügyvezetője, Uhrin Gábor okleveles gépészmérnök, rendszerszervező irányítja.</p>[/et_pb_text][/et_pb_column][/et_pb_row][/et_pb_section][et_pb_section fb_built=\"1\" prev_background_color=\"rgba(224,43,32,0.2)\" _builder_version=\"4.7.6\" _module_preset=\"default\" background_color_gradient_end=\"#ffffff\" top_divider_style=\"arrow3\" bottom_divider_color=\"#fc9569\"][et_pb_row column_structure=\"1_2,1_2\" _builder_version=\"4.7.7\" _module_preset=\"default\" custom_margin=\"-31px|auto||auto||\" custom_padding=\"||0px|||\"][et_pb_column type=\"1_2\" _builder_version=\"4.7.5\" _module_preset=\"default\"][et_pb_text _builder_version=\"4.7.7\" _module_preset=\"default\" text_text_color=\"#000000\" custom_margin=\"||-1px||false|false\" custom_padding=\"283px|||||\"][/et_pb_text][/et_pb_colu